import React, { useRef, useState } from "react";
import "./HomePage1.css";
import Payment from "./Payment";

function HomePage1() {
  const [showPayment, setShowPayment] = useState(false);
  const aboutRef = useRef(null); // ðŸ‘‰ create ref for about section
  const EventsRef = useRef(null);

  const handleDonateClick = () => {
    setShowPayment(true);
  };

  const handleClose = () => {
    setShowPayment(false);
  };

  const scrollToAbout = () => {
    aboutRef.current?.scrollIntoView({ behavior: "smooth" });
  };
const scrollToImageSlider = () => {
    EventsRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div>
      {/* Hero Section */}
      <div
        className="container"
        style={{
          background:
            'linear-gradient(rgba(29, 90, 140, 0.7), rgba(29, 90, 140, 0.7)), url("/arn.jpg") center/cover no-repeat',
        }}
      >
        <nav className="nav">
          <div className="logoSection">
            <div className="logo">
              <img src="/sevauk-logo.jpg" alt="Sevauk Logo" />
            </div>
            <div className="logo">
              <img src="/logo22.jpg" alt="Arunodaya Logo" />
            </div>
            <span className="title">ARUNODAYA</span>
          </div>

          <div className="navLinks">
            <a href="#">Home</a>
            <a className="linkBtn" onClick={scrollToAbout}>About</a>
            <a className="linkBtn" onClick={scrollToImageSlider}>Events</a>
            <button className="loginBtn" onClick={handleDonateClick}>
              Donate Us
            </button>
          </div>
        </nav>

        <div className="contentCenter">
          <div className="slogan">
            <span>BEHIND EVERY STEP OUR CHILDREN TAKE</span>
            <br />
            <span>THERE ARE HANDS THAT HOLD, HEARTS THAT CARE,</span>
            <br />
            <span>AND WHO BELIEVE IN THEIR POTENTIAL.</span>
          </div>
          <button
            className="enrollBtn"
            onClick={() => (window.location.href = "#contact")}
          >
            Contact Us
          </button>
        </div>

        <button className="donateSticky" onClick={handleDonateClick}>
          Donate Us
        </button>

        {/* Slide-up Payment Modal */}
        {showPayment && (
          <div className="paymentModal">
            <div className="modalOverlay" onClick={handleClose}></div>
            <div className="slideUpModal">
              <div className="modalHeader">
                <h2>Donate to Arunodaya</h2>
                <button className="closeBtn" onClick={handleClose}>
                  Ã—
                </button>
              </div>
              <div className="modalBody">
                <Payment />
              </div>
            </div>
          </div>
        )}
      </div>

      {/* ðŸ‘‰ About Us Section */}
      <div ref={aboutRef} className="about-section">
        <h2 className="school-subheading">About Us</h2>
        <p className="school-about-text">
          Arunodaya Special School began its journey in a small rented room with just 8â€“12 students, supported by three passionate faculty members and limited resources. What started as a humble initiative has today grown into a recognized institution for children with special needs in Gadag.

          The school is currently training 42 children, while more than 120 parents seek counselling and guidance from the team. Over the years, Arunodaya has trained 183 children, with 29 successfully integrated into mainstream schools. Among them, one proud student went on to complete a postgraduate degree in Social Work â€” a milestone that reflects the schoolâ€™s vision and impact.

          Arunodaya strongly believes in holistic development. Children here are nurtured not only through academics but also through arts, physical education, medical support, and specialized therapies. The focus is on empowering each child to achieve independence, confidence, and dignity in life.

          The school is equally committed to supporting families by providing counselling, awareness programs, and guidance to parents of special children. This two-way approach strengthens both the childâ€™s and the familyâ€™s journey.
        </p>
        <hr className="school-hr" />
      </div>
    </div>
  );
}

export default HomePage1;
