import React from "react";

const pins = [
  { year: "2015", x: 50, y: 20, title: "Univariety Journey Begins", details: [] },
  { year: "2016", x: 150, y: 80, title: "Early Growth", details: ["Partnered with 100+ schools.", "Collaborated with UCLA Extension"] },
  { year: "2017", x: 50, y: 140, title: "First Round of Investment", details: ["Investment from InfoEdge"] },
  // Continue pins at appropriate coordinates on the curved path
];

export default function RoadmapCurve() {
  return (
    <svg width="800" height="400" style={{ background: "#f9f9f9" }}>
      {/* Curved road path */}
      <path
        d="M 40 20 C 200 50 200 150 40 180 S 40 300 200 350"
        fill="none"
        stroke="#0c2f56"
        strokeWidth="20"
        strokeLinecap="round"
        filter="url(#shadow)"
      />
      
      <defs>
        <filter id="shadow" x="-20%" y="-20%" width="140%" height="140%">
          <feDropShadow dx="3" dy="3" stdDeviation="2" floodColor="#888" floodOpacity="0.5" />
        </filter>
      </defs>

      {/* Pins */}
      {pins.map((pin, i) => (
        <g key={i}>
          <circle
            cx={pin.x}
            cy={pin.y}
            r={30}
            fill={`url(#pin-gradient-${i % 3})`}
            stroke="#fff"
            strokeWidth={4}
          />
          <text x={pin.x} y={pin.y + 5} fill="#fff" fontWeight="bold" fontSize={16} textAnchor="middle" pointerEvents="none">
            {pin.year}
          </text>

          {/* Text content (simplified) */}
          <text x={pin.x + 50} y={pin.y} fill="#222" fontSize={14}>
            {pin.title}
          </text>
        </g>
      ))}

      <defs>
        <radialGradient id="pin-gradient-0" cx="0.5" cy="0.5" r="0.5">
          <stop offset="0%" stopColor="#fa5b2d" />
          <stop offset="100%" stopColor="#fd795e" />
        </radialGradient>
        <radialGradient id="pin-gradient-1" cx="0.5" cy="0.5" r="0.5">
          <stop offset="0%" stopColor="#6245df" />
          <stop offset="100%" stopColor="#9c8ff0" />
        </radialGradient>
        <radialGradient id="pin-gradient-2" cx="0.5" cy="0.5" r="0.5">
          <stop offset="0%" stopColor="#b23554" />
          <stop offset="100%" stopColor="#d7758e" />
        </radialGradient>
      </defs>
    </svg>
  );
}
