import React from 'react';
import './TestimonialCard.css'; // This assumes styles are still in a separate file

const TestimonialCard = ({ name, designation, text, rating, image }) => {
  return (
    <div className="testimonial-card">
      <div className="card-left">
        <h4 className="sample-name">{name}</h4>
        <p className="designation">{designation}</p>
        <div className="stars">
          {[...Array(5)].map((_, i) => (
            <span key={i} className={i < rating ? 'star filled' : 'star'}>â˜…</span>
          ))}
        </div>
        <p className="testimonial-text">{text}</p>
      </div>
      <div className="card-right">
        <img src={image} alt={name} className="profile-img" />
      </div>
    </div>
  );
};

function App() {
  const testimonials = [
    {
      name: 'Sample Name',
      designation: 'Designation',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas porttitor congue.',
      rating: 4,
    },
    {
      name: 'Sample Name',
      designation: 'Designation',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas porttitor congue.',
      rating: 5,
    },
    {
      name: 'Sample Name',
      designation: 'Designation',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas porttitor congue.',
      rating: 2,
    },
    {
      name: 'Sample Name',
      designation: 'Designation',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas porttitor congue.',
      rating: 3,
    }
  ];

  return (
    <div className="app">
      <h2>This is your sample text here. Enter your text here</h2>
      <div className="testimonial-grid">
        {testimonials.map((t, index) => (
          <TestimonialCard key={index} {...t} />
        ))}
      </div>
    </div>
  );
}

export default App;
