import React from "react";
import "./Academics.css";
// import { BookOpen } from "lucide-react"; // nice icon

const Academics = () => {
  const curriculum = [
    { text: "Academic Studies", color: "orange" },
    { text: "Environmental Education", color: "red" },
    { text: "Activities Of Daily Living", color: "green" },
    { text: "Therapeutic Interventions", color: "blue" },
    { text: "Physio Therapy", color: "green" },
    { text: "Speech Therapy", color: "orange" },
  ];

  const extraCurricular = [
    { text: "Computer Application", color: "orange" },
    { text: "Vocational Training", color: "red" },
    { text: "Drawing And Craft", color: "green" },
    { text: "Dance And Movement Therapy", color: "blue" },
    { text: "Yoga", color: "green" },
    { text: "Sports And Games", color: "orange" },
  ];

  const renderCards = (items) =>
    items.map((item, index) => (
      <div className={`card ${item.color}`} key={index}>
        <BookOpen size={22} className="icon" />
        <span>{item.text}</span>
      </div>
    ));

  return (
    <section className="academics-section">
      <h1 className="title">ACADEMICS</h1>

      <h2 className="subtitle">CURRICULUM</h2>
      <div className="cards-grid">{renderCards(curriculum)}</div>

      <h2 className="subtitle">EXTRA CURRICULAR ACTIVITIES</h2>
      <div className="cards-grid">{renderCards(extraCurricular)}</div>

      <button className="view-all-btn">View All</button>
    </section>
  );
};

export default Academics;
