import React from "react";
import roadmapSvg from "./roadmap.svg"; // Your curved road SVG exported from design tool
import "./RoadmapSvg.css";

// Data for pins and content with pixel positions relative to SVG viewport
const pinsData = [
  {
    year: "2015",
    x: 120,
    y: 30,
    title: "Univariety Journey Begins",
    details: []
  },
  {
    year: "2016",
    x: 300,
    y: 130,
    title: "Early Growth",
    details: [
      "Partnered with 100+ schools.",
      "Collaborated with UCLA Extension to introduce the Global Career Counsellor Program in India"
    ]
  },
  {
    year: "2017",
    x: 120,
    y: 250,
    title: "First Round of Investment",
    details: [
      "Received the first round of investment from InfoEdge, India’s pioneer in internet products"
    ]
  },
  {
    year: "2018",
    x: 300,
    y: 360,
    title: "Innovations with Career Guidance",
    details: [
      "Launched ‘Future 50 Schools Shaping Success’ in association with Fortune India magazine",
      "Super Counsellor SaaS Platform Launched for Career Counsellors"
    ]
  },
  {
    year: "2020",
    x: 120,
    y: 470,
    title: "More Investment & National Recognition",
    details: [
      "InfoEdge invests again to scale the alumni platform offerings.",
      "Our CEO, Mr Jaideep Gupta received the ‘Edutech Leader Award’ at the ET NOW Business Leader of the Year 2020 awards."
    ]
  },
  {
    year: "2022",
    x: 300,
    y: 590,
    title: 'Launched "Alumni Services as Software"',
    details: [
      "Partnered with 350+ schools for Alumni solutions",
      "AI Enabled Innovations in Alumni Onboarding, Engagement and Recognition"
    ]
  },
  {
    year: "2024",
    x: 120,
    y: 720,
    title: "Looking Ahead",
    details: [
      "Future plans and developments"
    ]
  }
];

export default function RoadmapSvg() {
  return (
    <div className="roadmap-svg-container">
      <img src={roadmapSvg} alt="Curved Road Path" className="roadmap-svg-image" />
      {pinsData.map((pin, i) => (
        <div 
          key={pin.year} 
          className={`roadmap-pin pin-${i}`} 
          style={{ left: pin.x, top: pin.y }}
        >
          <div className="pin-circle">{pin.year}</div>
          <div className="pin-content">
            <h4>{pin.title}</h4>
            {pin.details.length > 0 && (
              <ul>
                {pin.details.map((d, i) => <li key={i}>{d}</li>)}
              </ul>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}
