import React from "react";
import "./SupportGroups.css";

const supportGroups = [
  {
    name: "Nayi Disha Hyderabad 2",
    location: "Hyderabad",
    conditions: ["Autism", "ADHD", "Down Syndrome", "Intellectual Disability", "Cerebral Palsy"],
    language: "English",
  },
  {
    name: "Nayi Disha Bengaluru",
    location: "Bengaluru",
    conditions: ["Autism", "ADHD", "Down Syndrome", "Intellectual Disability", "Cerebral Palsy"],
    language: "English",
  },
  {
    name: "Nayi Disha Pune",
    location: "Pune",
    conditions: ["Autism", "ADHD", "Down Syndrome", "Intellectual Disability", "Cerebral Palsy"],
    language: "English",
  },
  {
    name: "Nayi Disha Down Syndrome",
    location: "Multi-city",
    conditions: ["Down Syndrome", "Intellectual Disability"],
    language: "English",
  },
  {
    name: "Nayi Disha Cerebral Palsy",
    location: "Multi-city",
    conditions: ["Cerebral Palsy", "Autism", "ADHD", "Down Syndrome", "Intellectual Disability"],
    language: "English",
  },
  {
    name: "Nayi Disha Mumbai",
    location: "Mumbai",
    conditions: ["Autism", "ADHD", "Down Syndrome", "Intellectual Disability", "Cerebral Palsy"],
    language: "English",
  },
];

export default function SupportGroups() {
  return (
    <div className="support-groups-container">
      <header className="header">
        <h1>üåº Parent Support Groups</h1>
        <p>Explore communities built for connection, care, and shared understanding.</p>
      </header>

      <div className="groups-grid">
        {supportGroups.map((group, index) => (
          <div key={index} className="group-card">
            <h2>{group.name}</h2>
            <p><strong>üìç Location:</strong> {group.location}</p>
            <p><strong>üß† Conditions Supported:</strong> {group.conditions.join(", ")}</p>
            <p><strong>üó£Ô∏è Language:</strong> {group.language} speaking group</p>
          </div>
        ))}
      </div>

      <footer className="donate-footer">
        <button className="donate-button">üå± Donate Us</button>
      </footer>
    </div>
  );
}
