import React, { useState, useEffect, useRef } from "react";
import "./ImageSlider.css";

// Divide images into two sections
const eventSlides = [
  { url: "/image1.jpg", title: "Collaboration" },
  { url: "/image2.jpg", title: "Innovation" },
  { url: "/image3.jpg", title: "Curiosity" },
];
const activitySlides = [
  { url: "/image4.jpg", title: "Impact" },
  { url: "/image5.jpg", title: "Learning" },
  { url: "/image6.jpg", title: "Growth" },
];

const AUTO_PLAY_DELAY = 3000;

const ImageSlider = () => {
  const [section, setSection] = useState("event");
  const [current, setCurrent] = useState(0);
  const startX = useRef(0);
  const endX = useRef(0);

  // Set correct slides based on section
  const slides = section === "event" ? eventSlides : activitySlides;

  useEffect(() => {
    const interval = setInterval(() => nextSlide(), AUTO_PLAY_DELAY);
    return () => clearInterval(interval);
  }, [current, slides]);

  const prevSlide = () =>
    setCurrent((prev) => (prev === 0 ? slides.length - 1 : prev - 1));
  const nextSlide = () =>
    setCurrent((prev) => (prev + 1) % slides.length);

  // Reset current index when section changes
  useEffect(() => {
    setCurrent(0);
  }, [section]);

  const handleTouchStart = (e) => {
    startX.current = e.touches[0].clientX;
  };
  const handleTouchEnd = (e) => {
    endX.current = e.changedTouches[0].clientX;
    if (startX.current - endX.current > 50) nextSlide();
    if (startX.current - endX.current < -50) prevSlide();
  };

  return (
    <div>
      {/* Section Buttons */}
      <div className="section-buttons">
        <button
          className={section === "event" ? "active" : ""}
          onClick={() => setSection("event")}
        >
          Events
        </button>
        <button
          className={section === "activity" ? "active" : ""}
          onClick={() => setSection("activity")}
        >
          Activities
        </button>
      </div>
      {/* Carousel */}
      <div
        className="carousel-container"
        onTouchStart={handleTouchStart}
        onTouchEnd={handleTouchEnd}
      >
        <button className="arrow left" onClick={prevSlide}>‚ùÆ</button>
        <div className="carousel">
          {slides.map((slide, idx) => {
            let offset = idx - current;
            if (offset < -Math.floor(slides.length / 2)) offset += slides.length;
            if (offset > Math.floor(slides.length / 2)) offset -= slides.length;
            const transform = `
              translateX(${offset * 240}px) 
              translateZ(${-Math.abs(offset) * 130}px) 
              rotateY(${offset * 15}deg)
              scale(${offset === 0 ? 1.3 : 0.9})
            `;
            const zIndex = slides.length - Math.abs(offset);
            return (
              <div
                key={idx}
                className={`carousel-slide${offset === 0 ? " active" : ""}`}
                style={{ transform, zIndex }}
              >
                <div className="photo-frame">
                  <img src={slide.url} alt={slide.title} />
                </div>
                <p>{slide.title}</p>
                <div className="reflection" />
              </div>
            );
          })}
        </div>
        <button className="arrow right" onClick={nextSlide}>‚ùØ</button>
      </div>
    </div>
  );
};

export default ImageSlider;
