import React from "react";
import "./Roadmap.css";

const roadmapData = [
  {
    year: "2015",
    title: "Univariety Journey Begins",
    details: []
  },
  {
    year: "2016",
    title: "Early Growth",
    details: [
      "Partnered with 100+ schools.",
      "Collaborated with UCLA Extension to introduce the Global Career Counsellor Program in India"
    ]
  },
  {
    year: "2017",
    title: "First Round of Investment",
    details: [
      "Received the first round of investment from InfoEdge, India’s pioneer in internet products"
    ]
  },
  {
    year: "2018",
    title: "Innovations with Career Guidance",
    details: [
      "Launched ‘Future 50 Schools Shaping Success’ in association with Fortune India magazine",
      "Super Counsellor SaaS Platform Launched for Career Counsellors"
    ]
  },
  {
    year: "2020",
    title: "More Investment & National Recognition",
    details: [
      "InfoEdge invests again to scale the alumni platform offerings.",
      "Our CEO, Mr Jaideep Gupta received the ‘Edutech Leader Award’ at the ET NOW Business Leader of the Year 2020 awards."
    ]
  },
  {
    year: "2022",
    title: 'Launched "Alumni Services as Software"',
    details: [
      "Partnered with 350+ schools for Alumni solutions",
      "AI Enabled Innovations in Alumni Onboarding, Engagement and Fundraising"
    ]
  },
  {
    year: "2024",
    title: "Looking Ahead",
    details: [
      "Future plans and developments"
    ]
  }
];

export default function Roadmap() {
  return (
    <div className="roadmap-container">
      <h2 className="roadmap-title">Roadmap</h2>
      <div className="roadmap-path">
        {roadmapData.map((item, index) => (
          <div key={item.year} className="roadmap-point-wrapper">
            <div
              className={`roadmap-point circle-${index % 7}`}
              aria-label={`Year ${item.year}`}
            >
              {item.year}
            </div>
            <div className="roadmap-content">
              <h3 className="roadmap-year">{item.year}</h3>
              <h4 className="roadmap-heading">{item.title}</h4>
              {item.details.length > 0 && (
                <ul className="roadmap-details">
                  {item.details.map((detail, i) => (
                    <li key={i}>{detail}</li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
