import React, { useState } from "react";
import "./StaffSection.css";

const Members = [
  { name: "Basavaraj Naglapur", role: "Member", image: "/edit1.jpg" },
  { name: "Lukkannasa Rajoli", role: "Member", image: "/edit2.jpg" },
  { name: "Umakanatasa Merwade", role: "HR, Finance & Administration", image: "/edit3.jpg" },
  { name: "Prakash Vailaya", role: "Academics", image: "/edit4.jpg" },
  { name: "Basavaraj Pattanshetty", role: "Projects and CSR", image: "/edit7.jpg" },
  { name: "Jaiprakash Bhanjetri", role: "Family Services", image: "/edit8.jpg" },
  { name: "Jayaraj Mulgund", role: "HR, Finance & Administration", image: "/edit9.jpg" },
  { name: "Lalit Jain", role: "Academics", image: "/edit10.jpg" },
  
];


const StaffMembers = [
  { name: "Basavaraj Pattanshetty", role: "Projects and CSR", image: "/edit7.jpg" },
  { name: "Jaiprakash Bhanjetri", role: "Family Services", image: "/edit8.jpg" },
  { name: "Jayaraj Mulgund", role: "HR, Finance & Administration", image: "/edit9.jpg" },
  { name: "Lalit Jain", role: "Academics", image: "/edit10.jpg" },
];

export default function StaffSection() {
  const [tab, setTab] = useState("Staff");

  let people;
  switch (tab) {
    case "Members":
      people = Members;
      break;
    default:
      people = StaffMembers;
  }

  return (
    <div className="Staff-container">
      <h1 className="Staff-title">Staff</h1>
      <div className="Staff-inner">
        <div className="Staff-tabs">
          {["Members", "Staff"].map((t) => (
            <div
              key={t}
              className={`Staff-tab ${tab === t ? "Staff-tab-active" : ""}`}
              onClick={() => setTab(t)}
              style={{ cursor: "pointer" }}
            >
              {t}
            </div>
          ))}
        </div>
        <div className="Staff-members">
          {people.map((tm) => (
            <div key={tm.name} className="Staff-member">
              <div className="Staff-member-image">
                <img
                  src={tm.image}
                  alt={tm.name}
                  style={{
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                    borderRadius: "20px",
                  }}
                />
              </div>
              <div className="Staff-member-name">{tm.name}</div>
              <div className="Staff-member-role">{tm.role}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
