import React, { useState } from "react";
import "./TeamSection.css";


const founders = [
  { name: "Anand Krishnan", role: "Founder", image: null },
  { name: "Sujata Satyamadhava", role: "Founder", image: null },
   { name: "Sujata Satyamadhava", role: "Family Services", image: null },
  { name: "Jyoti Rajappa", role: "HR, Finance & Administration", image: null },
  { name: "Priya Vijayan", role: "Academics", image: null },
];
const governors = [
  { name: "Jyoti Rajappa", role: "Governor", image: null },
  { name: "Priya Vijayan", role: "Governor", image: null }
];
const teamMembers = [
  { name: "Anand Krishnan", role: "Projects and CSR", image: null },
  { name: "Sujata Satyamadhava", role: "Family Services", image: null },
  { name: "Jyoti Rajappa", role: "HR, Finance & Administration", image: null },
  { name: "Priya Vijayan", role: "Academics", image: null },
];

export default function TeamSection() {
  const [tab, setTab] = useState("TEAM");
  const [imagesState, setImagesState] = useState({});

  let people;
  switch (tab) {
    case "FOUNDERS":
      people = founders;
      break;
    case "GOVERNORS":
      people = governors;
      break;
    default:
      people = teamMembers;
  }

  const handleImageChange = (e, idx) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (ev) => {
        setImagesState(prev => ({
          ...prev,
          [tab + idx]: ev.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="team-container">
      <h1 className="team-title">TEAM</h1>
      <div className="team-inner">
        <div className="team-tabs">
          {["FOUNDERS", "GOVERNORS", "TEAM"].map(t => (
            <div
              key={t}
              className={`team-tab ${tab === t ? "team-tab-active" : ""}`}
              onClick={() => setTab(t)}
              style={{ cursor: "pointer" }}
            >
              {t}
            </div>
          ))}
        </div>
        <div className="team-members">
          {people.map((tm, idx) => (
            <div key={tm.name} className="team-member">
              <div className="team-member-image">
                {imagesState[tab + idx] ? (
                  <img
                    src={imagesState[tab + idx]}
                    alt={tm.name}
                    style={{
                      width: "100%",
                      height: "100%",
                      objectFit: "cover",
                      borderRadius: "20px"
                    }}
                  />
                ) : (
                  <span role="img" aria-label="profile" className="profile-img">
                    ADD
                  </span>
                )}
                <input
                  type="file"
                  accept="image/*"
                  style={{
                    position: "absolute",
                    opacity: 0,
                    width: "100%",
                    height: "100%",
                    cursor: "pointer"
                  }}
                  title="Choose a profile picture"
                  onChange={e => handleImageChange(e, idx)}
                />
              </div>
              <div className="team-member-name">{tm.name}</div>
              <div className="team-member-role">{tm.role}</div>
            </div>
          ))}
        </div>
      </div>
      </div>

  );
}
