import React, { useState, useEffect, useRef, useCallback } from "react";
import "./ImageSlider.css";

const images = [
  "/image1.jpg",
  "/image2.jpg",
  "/image3.jpg",
  "/image4.jpg",
];

const ImageSlider = () => {
  const [current, setCurrent] = useState(0);
  const length = images.length;
  const timeoutRef = useRef(null);
  const AUTO_PLAY_DELAY = 3000; // ms

  // ✅ useCallback so it's safe to use in useEffect
  const startAutoPlay = useCallback(() => {
    clearTimeout(timeoutRef.current);
    timeoutRef.current = setTimeout(() => {
      setCurrent((prev) => (prev === length - 1 ? 0 : prev + 1));
    }, AUTO_PLAY_DELAY);
  }, [length]);

  useEffect(() => {
    startAutoPlay();
    return () => clearTimeout(timeoutRef.current);
  }, [current, startAutoPlay]); // ✅ no lint warning now

  const nextSlide = () => {
    setCurrent((prev) => (prev === length - 1 ? 0 : prev + 1));
  };

  const prevSlide = () => {
    setCurrent((prev) => (prev === 0 ? length - 1 : prev - 1));
  };

  const goToSlide = (idx) => {
    setCurrent(idx);
  };

  const pauseAutoPlay = () => clearTimeout(timeoutRef.current);

  return (
    <div
      className="slider"
      onMouseEnter={pauseAutoPlay}
      onMouseLeave={startAutoPlay}
    >
      <button className="arrow left" onClick={prevSlide} aria-label="Previous">
        &#10094;
      </button>
      <div className="slide-wrapper">
        {images.map((img, idx) => (
          <div
            key={idx}
            className={`slide${idx === current ? " active" : ""}`}
            style={{ backgroundImage: `url(${img})` }}
          />
        ))}
      </div>
      <button className="arrow right" onClick={nextSlide} aria-label="Next">
        &#10095;
      </button>
      <div className="dots">
        {images.map((_, idx) => (
          <span
            key={idx}
            className={`dot${idx === current ? " active" : ""}`}
            onClick={() => goToSlide(idx)}
          />
        ))}
      </div>
    </div>
  );
};

export default ImageSlider;
